import React from "react";
import "./App.css";
import { BrowserRouter as Router, Routes, Route, Link, Outlet } from "react-router-dom";
// import FirstPage from "./pages/RulesPage.js";
import RegisterPage from "./pages/RegisterPage.js";
import LoginPage from "./pages/LoginPage.js"; // Importing the LoginPage as a separate component
import RulesPage from "./pages/RulesPage.js"; // A page to display game rules
import Quiz from "./pages/Quiz.js";
function App() {
  return (
    <Router>
      <div className="App">
        {/* Header Section */}
        <header className="app-header">
          <h1>Welcome to the Ultimate Quiz Game!</h1>

            <Link to="/" className="nav-link">Home</Link>
            <Link to="/register" className="nav-link">Register</Link>
            <Link to="/login" className="nav-link">Login</Link>
            <Link to="/quiz" className="nav-link">Quiz</Link>
            <Outlet/>
        </header>

        {/* Main Content */}
        <main>
          <Routes>
            <Route index element={<RulesPage />} /> {/* Home displays rules */}
            <Route path="/RegisterPage" element={<RegisterPage />} />
            <Route path="/Login" element={<LoginPage />} />
            {/* <Route path="/FirstPage" element={<FirstPage />}/> */}
            <Route path="/quiz" element={<Quiz />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}
export default App;
